mapper.select:
SELECT *
FROM CART
WHERE ID=#{id}
AND PSEQ=#{pseq}
AND OSEQ=0

mapper.update:
UPDATE CART
SET MYCOUNT=#{myCount}
WHERE CART_SEQ=#{cart_seq}

UPDATE CART
SET OSEQ=#{oseq}
WHERE CART_SEQ=#{cart_seq}

UPDATE PRODUCT
SET PCOUNT=PCOUNT-#{myCount}
WHERE PRODUCT_SEQ=#{pseq}

UPDATE CART
SET GSEQ=#{goods_seq}
WHERE CART_SEQ=#{gcseq}

UPDATE PRODUCT
SET PPOINT=#{ppoint}, HCOUNT=#{hcount}
WHERE PRODUCT_SEQ=#{product_seq}
